mapper.select:
SELECT d.drk_order_no, drk_order_name, fc_name, drk_start_date, drk_pay_method FROM drk_order d JOIN franchise f ON d.fc_no = f.fc_no JOIN drk_pay p ON d.drk_order_no = p.drk_order_no WHERE member_no = #{member_no}
AND d.drk_status IN ( ${drinkingType} )
ORDER BY drk_order_date DESC

SELECT drk_receiver, drk_tel, drk_addr, drk_addr_detail, drk_ship_loc
FROM drk_ship
WHERE drk_order_no = #{drk_order_no}

mapper.update:
UPDATE drk_order
SET drk_order_name = #{drk_order_name}
WHERE drk_order_no = #{drk_order_no}

mapper.select:
SELECT acnt_holder, bank_name
FROM acnt_info a JOIN bank b ON a.bank_no = b.bank_no
WHERE acnt_info_no = #{pay_info_no}

mapper.update:
UPDATE drk_order
SET drk_status = 1
WHERE drk_order_no = #{drk_order_no}

UPDATE drk_schedule
SET drk_end_date = #{drk_end_date}
WHERE drk_order_no = #{drk_order_no}
